function Row(props) {
  return (
    <div className="row">
      <div className="cell">
        {(!props.editMode || props.x.id !== props.editId) &&
          props.x.productName}
        {props.editMode && props.x.id === props.editId && (
          <input
            type="text"
            value={props.nameValue}
            onChange={(e) => {
              props.setNameValue(e.target.value);
            }}
          />
        )}
      </div>
      <div className="cell">
        {(!props.editMode || props.x.id !== props.editId) &&
          props.x.productPrice}
        {props.editMode && props.x.id === props.editId && (
          <input
            type="text"
            value={props.priceValue}
            onChange={(e) => {
              props.setPriceValue(e.target.value);
            }}
          />
        )}
      </div>
      <div className="cell">
        {(!props.editMode || props.x.id !== props.editId) &&
          props.x.productManufactorer}
        {props.editMode && props.x.id === props.editId && (
          <input
            type="text"
            value={props.manufactorerValue}
            onChange={(e) => {
              props.setManufactorerValue(e.target.value);
            }}
          />
        )}
      </div>
      <div className="cell">
        {!props.editMode && (
          <>
            <button
              className="editar"
              id={props.x.id}
              onClick={() => {
                props.rowEdit(props.x);
              }}
            >
              Edit
            </button>
            <button
              className="remover"
              id={props.x.id}
              onClick={() => {
                props.deleteData(props.x.id);
              }}
            >
              Delete
            </button>
          </>
        )}
        {props.editMode && props.x.id === props.editId && (
          <>
            <button
              className="save"
              id={props.x.id}
              onClick={() => {
                props.patchData(props.x.id);
              }}
            >
              Save
            </button>
            <button
              className="cancel"
              id={props.x.id}
              onClick={() => {
                props.rowEditCancel("0");
              }}
            >
              Cancel
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default Row;
