import { useEffect, useState } from "react";
import Row from "./row/Row";

function Datas() {
  const apiUrl =
    "https://614b8c82e4cc2900179eb140.mockapi.io/api/v1/restaurants";
  const [datas, setDatas] = useState([]);

  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(""); //current selected id

  const [newNameValue, setNewNameValue] = useState("");
  const [newPriceValue, setNewPriceValue] = useState("");

  const [nameValue, setNameValue] = useState("");
  const [priceValue, setPriceValue] = useState("");

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    const result = await fetch(apiUrl);
    const getResult = await result.json();
    setDatas(getResult);
  }

  async function addData() {
    let newData = {
      productImage: "no",
      productName: newNameValue,
      productPrice: newPriceValue
    };
    const result = await fetch(apiUrl, {
      method: "POST",
      body: JSON.stringify(newData),
      headers: {
        "Content-Type": "application/json"
      }
    });
    const getResult = await result.json();
    console.log(getResult);
    setEditId(0);
    setEditMode(false);
    getData(); //to refetch data afte patching
    setNewNameValue("");
    setNewPriceValue("");
  }

  async function patchData(id) {
    let patchData = {
      productName: nameValue,
      productPrice: priceValue
    };
    const result = await fetch(apiUrl + "/" + id, {
      method: "PUT",
      body: JSON.stringify(patchData),
      headers: {
        "Content-Type": "application/json"
      }
    });
    const getResult = await result.json();
    console.log(getResult);
    setEditId(0);
    setEditMode(false);
    getData(); //to refetch data afte patching
  }

  async function deleteData(id) {
    const result = await fetch(apiUrl + "/" + id, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json"
      }
    });
    const getResult = await result.json();
    console.log(getResult);
    setEditMode(false);
    getData(); //to refetch data afte patching
  }

  function rowEdit(e) {
    setEditId(e.id);
    setEditMode(!editMode);
    setNameValue(e.productName);
    setPriceValue(e.productPrice);
  }
  function rowEditCancel(e) {
    setEditId(e);
    setEditMode(!editMode);
  }
  function newName(e) {
    setNewNameValue(e.target.value);
  }
  function newPrice(e) {
    setNewPriceValue(e.target.value);
  }

  return (
    <div className="data_table">
      <div className="row">
        <div className="cell">
          <input type="text" value={newNameValue} onChange={newName} />
        </div>
        <div className="cell">
          <input type="text" value={newPriceValue} onChange={newPrice} />
        </div>
        <div className="cell">
          <button onClick={addData}>ADD</button>
        </div>
      </div>

      {datas.map((x) => {
        return (
          <Row
            key={x.id}
            x={x}
            editMode={editMode}
            editId={editId}
            nameValue={nameValue}
            setNameValue={setNameValue}
            priceValue={priceValue}
            setPriceValue={setPriceValue}
            rowEdit={rowEdit}
            deleteData={deleteData}
            patchData={patchData}
            rowEditCancel={rowEditCancel}
          />
        );
      })}
    </div>
  );
}

export default Datas;
